import React, { useState, useEffect } from 'react';
import './Global.css';
import './App.css';
import './Sidebar.css';
import './Main.css';
import api from './services/Api';
//Componente - Boloco isolado de HTML, CSS e JS, o qual nao interfere no restante da aplicação.
//Estado - Informaçoes mantidas pelo componente (Lembrar: Imutabilidade.)
//Propriedade - Informações que o componente PAI passa para o componente FILHO.

function App() {
  const [ latitude, setLatitude ] = useState('');
  const [ longitude, setLongitude ] = useState('');
  const [ github_username, setGithubUsername ] = useState('');
  const [ techs, setTechs ] = useState('');
  const [ devs, setDevs ] = useState([]);  

  useEffect(() => {
    navigator.geolocation.getCurrentPosition(
      (position) => {
        const { latitude, longitude } = position.coords;

        setLatitude(latitude);
        setLongitude(longitude);
      },
      (err) => {
        console.log(err);
      },
      {
        timeout: 30000,
      }
    )
  }, []);

  useEffect(() => {
    async function loadDevs() {
      const response = await api.get('/devs');
      setDevs(response.data);
    }
    loadDevs();
  }, []);

  async function handleAddDev(e) {
    e.preventDefault();

    const response = await api.post('/devs', {
      github_username,
      techs,
      latitude,
      longitude,
    })
      setGithubUsername('');
      setTechs('');
      setDevs([...devs, response.data]);
  }

  return (
    <div id="app">
      <aside>
        <strong>Cadastrar:</strong>
        <form onSubmit={handleAddDev}>
        <div className="input-block"> 
          <label htmlFor="github_username">Usuário Github</label>
          <input 
          name="github_username" 
          id="github_username" 
          required 
          value={github_username}
          onChange={e => setGithubUsername(e.target.value)}
          />
        </div> 

        <div className="input-block">
          <label htmlFor="techs">Tecnologias</label>
          <input 
          name="techs" 
          id="techs" 
          required 
          value={techs}
          onChange={e => setTechs(e.target.value)}
          />
        </div>

        <div className="input-group">
          <div className="input-block">
          <label htmlFor="latitude">Latitude</label>
          <input
           type="number"
            name="latitude"
             id="latitude" 
             required value={latitude}
             onChange= {e => setLatitude(e.target.value)}
             />
          </div>

          <div className="input-block">
          <label htmlFor="longitude">Longitude</label>
          <input 
          type="number" 
          name="longitude" 
          id="longitude" 
          required value={longitude}
          onChange= {e => setLongitude(e.target.value)}
          />
          </div>
        </div>
        <button type="submit">Salvar</button>
        </form>
      </aside>

      <main>
        <ul>
          {devs.map(dev =>(
            <li className="dev-item">
            <header>
              <img src={dev.avatar_url} alt={dev.name} />
              <div className="user-info">
                <strong>{dev.name}</strong>
                <span>{dev.techs.join(', ')}</span>
              </div>
            </header>
            <p>{dev.bio}</p>
            <a href={`https://github.com/${dev.github_username}`}>Acessar perfil no Github</a>
          </li>
           ))}
        </ul>
      </main>
    </div>
  );
}

export default App;
